import React from 'react';
import normal_copy from "../../assets/icon/normal_copy.svg"
import yellow_copy from "../../assets/icon/yellow_copy.svg"

const copy_icon = [normal_copy, yellow_copy];

function fallbackCopyTextToClipboard(text) {
    var textArea = document.createElement("textarea");
    textArea.value = text;

    // Avoid scrolling to bottom
    textArea.style.top = "0";
    textArea.style.left = "0";
    textArea.style.position = "fixed";

    document.body.appendChild(textArea);
    textArea.focus();
    textArea.select();

    try {
      var successful = document.execCommand('copy');
      var msg = successful ? 'successful' : 'unsuccessful';
      console.log('Fallback: Copying text command was ' + msg);
    } catch (err) {
      console.error('Fallback: Oops, unable to copy', err);
    }

    document.body.removeChild(textArea);
  }

function copyTextToClipboard() {
  var text = document.getElementById("summarize-text").innerHTML;
  if (!navigator.clipboard) {
    fallbackCopyTextToClipboard(text);
    return;
  }
  navigator.clipboard.writeText(text).then(function() {
    console.log('Async: Copying to clipboard was successful!');
  }, function(err) {
    console.error('Async: Could not copy text: ', err);
  });
}

export function CopyButton(){
    const ChangeIcon = (i) =>{
        var d = document.getElementById("copy-icon");
        d.src = copy_icon[i];
    }
    return (
        <img src={normal_copy} id="copy-icon"
        className='Copy-Icon' alt="copy-icon"
        onClick={()=>copyTextToClipboard()}
        onPointerEnter={()=>ChangeIcon(1)}
        onPointerLeave={()=>ChangeIcon(0)}
        />
    );
}
