import React, {Component} from 'react';
// import css
import "../css/App.css"
import "../css/index.css"

// import js function
import { GitIcon, LogoTop } from './Header';
import { OnSubmit } from './SubmitButton';
import { main } from './Params';

// import image file
import search from "../assets/icon/logo.svg"

// import Footer from "./Footer/Footer";
// import Header from "./Header/Header";

export default class Home extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            webLink: main.defautText,
            scrolldelay: null
        };
    }
    handleChange(){
        var getValue = document.getElementById("input-box");
        getValue.style.color = main.normal_text[0];
        this.setState({webLink: getValue.value});
    }
    handleOnClick(){
        var getValue = document.getElementById("input-box");
        console.log(getValue.defaultValue);
        if (getValue.value === main.defautText){
            getValue.value = "";
       }
       this.setState({webLink: getValue.value, clicked:true});
    }
    handleReset(){
        var getValue = document.getElementById("input-box");
        if(this.state.webLink===""){
            getValue.value=main.defautText;
            getValue.style.color = main.unselect_text[0];
        }
        getValue.style.borderWidth = main.unselect_text[1]
    }
    handleOnStay(){
        var getValue = document.getElementById("input-box");
        getValue.style.borderWidth = main.normal_text[1];
    }
    render() {
    return (
        <div className="App">
            <div className='Header'>
                <LogoTop id="logo-header"/>
                <GitIcon/>
            </div>
            <div className='Body'>
                <h1 className='HomeTitle'>
                    <img src={search} className='App-logo' alt='search'/>
                    Web<span>2</span>sum
                </h1>
                    <OnSubmit/>
                    <div id="dashboard" className='Start-lvl'>
                        <div id='webScrap' className='Rectangle' style={{height: "50vh"}}/>
                        <div id='summarize' className='Rectangle' style={{height: "35vh"}}/>
                    </div>
            </div>
        </div>
    )
  }
}
